package bradley.service;

import bradley.model.Customer;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class CustomerServiceImpl implements CustomerService {
    private static Map<Integer, Customer> customers;

    static {

        customers = new HashMap<>();
//        customers.put(1, new Customer(1, "John", "john@codegym.vn", "Hanoi", ""));
//        customers.put(2, new Customer(2, "Bill", "bill@codegym.vn", "Danang", "data:image/jpeg;base64,"));
        customers.put(1, new Customer(1, "John", "john@codegym.vn", "Hanoi", "Capture.png"));
        customers.put(2, new Customer(2, "Bill", "bill@codegym.vn", "Danang", "Capture.png"));
        customers.put(3, new Customer(3, "Alex", "alex@codegym.vn", "Saigon", "/resources/Capture.PNG"));
        customers.put(4, new Customer(4, "Adam", "adam@codegym.vn", "Beijin", "../Capture.PNG"));
        customers.put(5, new Customer(5, "Sophia", "sophia@codegym.vn", "Miami", "../Capture.PNG"));
        customers.put(6, new Customer(6, "Rose", "rose@codegym.vn", "Newyork", "../Capture.PNG"));
    }

    @Override
    public List findAll() {
        return new ArrayList<>(customers.values());
    }

    @Override
    public void save(Customer customer) {

        customers.put(customer.getId(), customer);
    }

    @Override
    public Customer findById(int id) {
        return customers.get(id);
    }

    @Override
    public void update(int id, Customer customer) {
        customers.put(id, customer);

    }

    @Override
    public void remove(int id) {
        customers.remove(id);
    }
}
